import { useState, useEffect, useRef } from 'react';

export default function useComponentVisible(initialIsVisible) {
    const [isComponentVisible, setIsComponentVisible] = useState(initialIsVisible);
    const ref = useRef(null);

    const handleClickOutside = (event) => {
        if (ref.current && !ref.current.contains(event.target)) {
            setIsComponentVisible(false);
        }
    };

    useEffect(() => {
        const parrentPosition =  ref.current.getBoundingClientRect()
        const dpowDown_content = document.getElementsByClassName('dpowDown_content')
        dpowDown_content.style.top = 20 + parrentPosition.top + 'px';
        dpowDown_content.style.left = -100 +parrentPosition.left+ 'px';
        console.log(dpowDown_content)
        document.addEventListener('click', handleClickOutside, true);
        return () => {
            document.removeEventListener('click', handleClickOutside, true);
        };
    });

    return { ref, isComponentVisible, setIsComponentVisible };
}
